import{_ as i,c as t,o as l,ag as n}from"./chunks/framework.B9E6zNJU.js";const c=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"de/prenc-lista.md","filePath":"de/prenc-lista.md"}'),r={name:"de/prenc-lista.md"};function u(s,e,o,a,m,h){return l(),t("div",null,[...e[0]||(e[0]=[n('<details><summary>1 Fall &quot;Unsichtbare Kompressionsunterwäsche&quot;</summary><ul><li>Kontext: PTBS-K relational - soziale Angst.</li><li>Prozedur: Kompressionsunterhemd unter formeller Kleidung während Sakramenten.</li><li>Funktion: &quot;Unsichtbare Umarmung&quot;, die Hypervigilanz reduziert.</li><li>Risiko: Gesehen als &quot;Übertreibung&quot; oder &quot;Mangel an Einfachheit&quot;.</li></ul></details><details><summary>2 Fall &quot;Tragbare Übergangsdecke&quot;</summary><ul><li>Kontext: Autismus - PTBS-K.</li><li>Prozedur: Eine kleine zusammengerollte Decke im Rucksack tragen zur Verwendung bei sensorischen Krisen.</li><li>Funktion: Behältnisobjekt in unvorhersehbaren Umgebungen.</li><li>Risiko: Als &quot;kindisch&quot; oder &quot;unangemessen&quot; bei Erwachsenen betrachtet.</li></ul></details><details><summary>3 Fall &quot;Doppelte Socken im Sommer&quot;</summary><ul><li>Kontext: Dissoziation durch frühes Trauma.</li><li>Prozedur: Zwei Paar dicke Socken tragen, auch bei Hitze.</li><li>Funktion: Propriozeptiver Anker zur Reduktion des &quot;Schwebens&quot;.</li><li>Risiko: Beurteilt als &quot;Vernachlässigung&quot; oder &quot;Mangel an Hygiene&quot;.</li></ul></details><details><summary>4 Fall &quot;Kleidung einer sicheren Figur&quot;</summary><ul><li>Kontext: Traumatische Bindung.</li><li>Prozedur: Immer einen Schal oder eine Weste einer Vertrauensperson tragen.</li><li>Funktion: Symbolische Aktivierung interner Bindungsnetzwerke.</li><li>Risiko: Fehlinterpretiert als &quot;emotionale Abhängigkeit&quot;.</li></ul></details><details><summary>5 Fall &quot;Nahtlose Unterwäsche&quot;</summary><ul><li>Kontext: Autismus - sensorische Hypersensibilität.</li><li>Prozedur: Ausschließliche Verwendung von Unterwäsche ohne Etiketten oder Nähte.</li><li>Funktion: Vermeidung somatosensorischer Überlastung.</li><li>Risiko: Gesehen als &quot;Launenhaftigkeit&quot; oder &quot;unnötige Anforderung&quot;.</li></ul></details><details><summary>6 Fall &quot;Nächtliche Baumwollhandschuhe&quot;</summary><ul><li>Kontext: Somatisierte PTBS-K.</li><li>Prozedur: Mit weichen Handschuhen schlafen, um taktile Hypersensibilität zu reduzieren.</li><li>Funktion: Sensorische Barriere, die Ruhe ermöglicht.</li><li>Risiko: Als &quot;seltsam&quot; oder &quot;zwanghaft&quot; betrachtet.</li></ul></details><details><summary>7 Fall &quot;Taktile Flasche in Besprechungen&quot;</summary><ul><li>Kontext: PTBS-K - soziale Angst.</li><li>Prozedur: Eine Flasche mit spezifischer Textur während Gesprächen halten.</li><li>Funktion: Sensorische Fokussierung zur Angstmodulation.</li><li>Risiko: Interpretiert als &quot;Mangel an Aufmerksamkeit&quot; oder &quot;Zappelei&quot;.</li></ul></details><details><summary>8 Fall &quot;Analoguhr mit Sekundenzeiger&quot;</summary><ul><li>Kontext: Zeitliche PTBS-K (chronobiologische Desynchronie).</li><li>Prozedur: Ausschließliche Verwendung einer Analoguhr mit sichtbarem Sekundenzeiger.</li><li>Funktion: Rhythmischer Anker zur Regulierung der Zeitwahrnehmung.</li><li>Risiko: Gesehen als &quot;veraltet&quot; oder &quot;unnötig&quot;.</li></ul></details><details><summary>9 Fall &quot;Personalisierte sensorische Einlagen&quot;</summary><ul><li>Kontext: Leichte Dissoziation - Autismus.</li><li>Prozedur: Schuhe mit Einlagen variabler Textur oder leichter Vibration.</li><li>Funktion: Kontinuierliche somatische Erdung.</li><li>Risiko: Als &quot;unbedeutendes Detail&quot; ignoriert.</li></ul></details><details><summary>10 Fall &quot;Symbolische kindliche Unterwäsche&quot;</summary><ul><li>Kontext: PTBS-K durch emotionale Vernachlässigung.</li><li>Prozedur: Verwendung von Unterwäsche mit &quot;kindlichen&quot; Designs (z.B.: Zeichnungen) unter Erwachsenenkleidung.</li><li>Funktion: Schaffung eines sicheren privaten Raums; körperliches Selbstmitgefühl.</li><li>Risiko: Fehlinterpretiert als Fetischismus oder Unreife.</li></ul></details><details><summary>11 Fall &quot;Feuchtes Innentuch mit warmem Wasser&quot;</summary><ul><li>Kontext: Somatisierte PTBS-K - Bedarf an vagaler Regulation.</li><li>Prozedur: Ein leicht angefeuchtetes Kleidungsstück gegen die Haut tragen.</li><li>Funktion: Thermische Stimulation, die das Nervensystem beruhigt.</li><li>Risiko: Verwechselt mit Vernachlässigung oder unangemessenem Verhalten.</li></ul></details><details><summary>12 Fall &quot;Symbolische nächtliche Windel&quot;</summary><ul><li>Kontext: Dissoziative PTBS-K durch kindliche Vernachlässigung.</li><li>Prozedur: Verwendung einer Stoffwindel mit Gummihose nur zum Schlafen.</li><li>Funktion: Taktile Eindämmung, symbolische Reparatur der Primärversorgung.</li><li>Risiko: Fehlinterpretiert als Regression oder Unreife.</li></ul></details><details><summary>13 Fall &quot;Sanfte Brustbandage&quot;</summary><ul><li>Kontext: Dissoziation durch relationales Trauma.</li><li>Prozedur: Nicht-restriktive elastische Bandage um den Rumpf.</li><li>Funktion: Gefühl &quot;enthalten zu sein&quot;, um Ich-Desintegration zu vermeiden.</li><li>Risiko: Fälschlicherweise mit Selbstverletzung oder Essstörungen assoziiert.</li></ul></details><details><summary>14 Fall &quot;Socken im Tempel&quot;</summary><ul><li>Kontext: Autismus - taktile Hypersensibilität.</li><li>Prozedur: Verwendung von Socken in Umgebungen, wo Barfußgehen erwartet wird.</li><li>Funktion: Sensorischen Schmerz reduzieren und körperliche Verbindung aufrechterhalten.</li><li>Risiko: Gesehen als Ungehorsam gegenüber lokalen Bräuchen.</li></ul></details><details><summary>15 Fall &quot;Unterwäsche des anderen Geschlechts (wegen Textur)&quot;</summary><ul><li>Kontext: PTBS-K - spezifisches sensorisches Bedürfnis.</li><li>Prozedur: Verwendung von Unterwäsche, die für das andere Geschlecht entwickelt wurde, wegen ihres Schnitts oder ihrer Weichheit.</li><li>Funktion: Taktile Regulation, nicht Geschlechtsidentität.</li><li>Risiko: Verwechselt mit Fragen der sexuellen Identität.</li></ul></details><details><summary>16 Fall &quot;Wegwerfwindel unter Anzug&quot;</summary><ul><li>Kontext: Dissoziative PTBS-K.</li><li>Prozedur: Verwendung einer Wegwerfwindel als Unterwäsche bei gesellschaftlichen Veranstaltungen.</li><li>Funktion: Konstante Eindämmung zur Vermeidung emotionalen Zusammenbruchs.</li><li>Risiko: Beurteilt als &quot;Täuschung&quot; oder &quot;Mangel an Reife&quot;.</li></ul></details><details><summary>17 Fall &quot;Nächtliche Bauchbinde&quot;</summary><ul><li>Kontext: Bauchtrauma - Gefühl emotionaler Leere.</li><li>Prozedur: Kleidungsstück sanft um den Bauch gewickelt zum Schlafen.</li><li>Funktion: &quot;Innere Stütze&quot;, die viszerale Angst reduziert.</li><li>Risiko: Mit Gewichtskontrolle oder Körperstörungen assoziiert.</li></ul></details><details><summary>18 Fall &quot;Unterwäsche aus Seide oder Neopren&quot;</summary><ul><li>Kontext: Autismus - Suche nach spezifischer taktiler Reaktion.</li><li>Prozedur: Verwendung unkonventioneller Materialien wegen ihrer regulatorischen Wirkung.</li><li>Funktion: Selektive Aktivierung beruhigender sensorischer Wege.</li><li>Risiko: Gesehen als &quot;unnötiger Luxus&quot; oder &quot;Exzentrizität&quot;.</li></ul></details><details><summary>19 Fall &quot;Schal als zweite Haut&quot;</summary><ul><li>Kontext: Relationale PTBS-K.</li><li>Prozedur: Immer einen Schal tragen, der Hals und Schultern umhüllt.</li><li>Funktion: Symbolisiert Schutz und zwischenmenschliche Grenze.</li><li>Risiko: Als &quot;übertrieben&quot; in heißen Klimaten betrachtet.</li></ul></details><details><summary>20 Fall &quot;Drehbarer Ring zur Selbstregulation&quot;</summary><ul><li>Kontext: PTBS-K - Flashbacks.</li><li>Prozedur: Verwendung eines Rings mit beweglichem Teil zum Reiben in Stressmomenten.</li><li>Funktion: Sensorische Unterbrechung traumatischer Aktivierung.</li><li>Risiko: Gesehen als &quot;Spielzeug&quot; oder &quot;Ablenkung&quot;.</li></ul></details><details><summary>21 Fall &quot;Tagespyjama zu Hause&quot;</summary><ul><li>Kontext: PTBS-K - regulatorische Erschöpfung.</li><li>Prozedur: Schlafkleidung tagsüber in sicheren Umgebungen tragen.</li><li>Funktion: Sensorische Anforderungen reduzieren und emotionale Energie bewahren.</li><li>Risiko: Beurteilt als &quot;Mangel an Anstrengung&quot; oder &quot;Depression&quot;.</li></ul></details><details><summary>22 Fall &quot;Leichte Gewichtsweste&quot;</summary><ul><li>Kontext: Autismus - sensorische Angst.</li><li>Prozedur: Weste mit verteiltem Gewicht (weniger als 10% des Körpergewichts).</li><li>Funktion: Propriozeptive Stimulation für neurologische Ruhe.</li><li>Risiko: Fehlinterpretiert als &quot;Bestrafung&quot; oder &quot;Einschränkung&quot;.</li></ul></details><details><summary>23 Fall &quot;Brille mit Bernsteinlichtfilter&quot;</summary><ul><li>Kontext: PTBS-K - visuelle Überlastung.</li><li>Prozedur: Verwendung von Brillen mit spezifischer Tönung in Innenräumen.</li><li>Funktion: Visuelle Stimulation reduzieren, die Alarm aktiviert.</li><li>Risiko: Gesehen als &quot;Affektiertheit&quot; oder &quot;Kuriosität&quot;.</li></ul></details><details><summary>24 Fall &quot;Taktile Armbänder am Handgelenk&quot;</summary><ul><li>Kontext: Dissoziation durch zwischenmenschliches Trauma.</li><li>Prozedur: Verwendung von Armbändern mit variablen Texturen.</li><li>Funktion: Taktiler Anker zur Aufrechterhaltung körperlicher Präsenz.</li><li>Risiko: Verwechselt mit Mode oder ästhetischem Accessoire.</li></ul></details><details><summary>25 Fall &quot;Unterwäsche aus der Kindheit (Erwachsenengröße)&quot;</summary><ul><li>Kontext: PTBS-K durch Bruch früher Bindung.</li><li>Prozedur: Kleidungsstück aus der Kindheit in Erwachsenengröße reproduzieren.</li><li>Funktion: Symbolische Wiederverbindung mit Stadium vor dem Trauma.</li><li>Risiko: Fehlinterpretiert als pathologische Fixierung.</li></ul></details><details><summary>26 Fall &quot;Mehrere Decken im Sommer&quot;</summary><ul><li>Kontext: Nächtliche PTBS-K - Angst vor Desintegration.</li><li>Prozedur: In mehrere Decken eingewickelt schlafen, auch bei Hitze.</li><li>Funktion: &quot;Sichere Gebärmutter&quot; für erholsamen Schlaf nachbilden.</li><li>Risiko: Gesehen als &quot;irrational&quot; oder &quot;selbstzerstörerisch&quot;.</li></ul></details><details><summary>27 Fall &quot;Armband mit Perlen zum Reiben&quot;</summary><ul><li>Kontext: PTBS-K - Hyperalertheit.</li><li>Prozedur: Armband mit texturierten Perlen für wiederholende Stimulation.</li><li>Funktion: Nonverbale Selbstregulation in sozialen Umgebungen.</li><li>Risiko: Beurteilt als &quot;Nervosität&quot; oder &quot;Mangel an Fassung&quot;.</li></ul></details><details><summary>28 Fall &quot;Unterhemd mit symbolischer Tasche&quot;</summary><ul><li>Kontext: PTBS-K - Bedarf an Übergangsobjekt.</li><li>Prozedur: Unterhemd mit kleiner interner Tasche, die ein sicheres Objekt enthält.</li><li>Funktion: Diskreter Zugang zu emotionalem Anker.</li><li>Risiko: Ignoriert oder fehlinterpretiert als &quot;seltsam&quot;.</li></ul></details><details><summary>29 Fall &quot;Schuhe immer gebunden, nie aufgemacht&quot;</summary><ul><li>Kontext: Autismus - Bedarf an sensorischer Vorhersagbarkeit.</li><li>Prozedur: Schuhe immer gut angepasst halten, ohne Spielraum.</li><li>Funktion: Gefühl der &quot;Trennung&quot; vom Boden vermeiden.</li><li>Risiko: Gesehen als &quot;Starrheit&quot; oder &quot;Obsession&quot;.</li></ul></details><details><summary>30 Fall &quot;Unterwäsche mit Affirmations-Etikett&quot;</summary><ul><li>Kontext: PTBS-K - internalisierte Scham.</li><li>Prozedur: Kleidungsstück mit eingenähtem Etikett mit &quot;Sicher&quot;, &quot;Würdig&quot;, etc.</li><li>Funktion: Symbolische Verstärkung des persönlichen Werts in Krisenmomenten.</li><li>Risiko: Unsichtbar gemacht oder als &quot;Aberglaube&quot; betrachtet.</li></ul></details> --- <div class="tip custom-block"><p class="custom-block-title">Willkommen</p><p>Hinweis: ClearPath ist eine unabhängige Bildungsressource, die von treuen Mitgliedern erstellt wurde, um Führungskräfte, Familien und Fachleute zu unterstützen. Sie ist nicht mit der Kirche Jesu Christi der Heiligen der Letzten Tage verbunden oder wird von dieser offiziell unterstützt.</p><p>Jeder Weg zur Klarheit beginnt mit einem ersten Schritt. Deiner beginnt hier.</p></div>',32)])])}const g=i(r,[["render",u]]);export{c as __pageData,g as default};
