import{_ as e,c as a,o,ag as l}from"./chunks/framework.B9E6zNJU.js";const p=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"es/prenc-lista.md","filePath":"es/prenc-lista.md"}'),s={name:"es/prenc-lista.md"};function n(r,i,t,c,d,u){return o(),a("div",null,[...i[0]||(i[0]=[l('<details><summary>1 Caso “Ropa Interior de Compresión Invisible”</summary><ul><li>Contexto: TEPT -C relacional - ansiedad social.</li><li>Procedimiento: Camiseta interior de compresión bajo ropa formal en sacramentales.</li><li>Función: “Abrazo invisible” que reduce hipervigilancia.</li><li>Riesgo: Visto como “exceso” o “falta de simplicidad”.</li></ul></details><details><summary>2 Caso “Manta Transicional Portátil”</summary><ul><li>Contexto: Autismo - TEPT -C.</li><li>Procedimiento: Llevar una manta pequeña enrollada en mochila para usar en crisis sensoriales.</li><li>Función: Objeto de contención en entornos impredecibles.</li><li>Riesgo: Considerado “infantil” o “inapropiado” en adultos.</li></ul></details><details><summary>3 Caso “Calcetines Dobles en Verano”</summary><ul><li>Contexto: Disociación por trauma temprano.</li><li>Procedimiento: Usar dos pares de calcetines gruesos incluso en calor.</li><li>Función: Anclaje propioceptivo para reducir sensación de “flotar”.</li><li>Riesgo: Juzgado como “descuido” o “falta de higiene”.</li></ul></details><details><summary>4 Caso “Prenda de Figura Segura”</summary><ul><li>Contexto: Apego traumático.</li><li>Procedimiento: Llevar siempre una bufanda o chaleco de una persona de confianza.</li><li>Función: Activación simbólica de redes de apego interno.</li><li>Riesgo: Malinterpretado como “dependencia emocional”.</li></ul></details><details><summary>5 Caso “Ropa Interior Sin Costuras”</summary><ul><li>Contexto: Autismo - hipersensibilidad sensorial.</li><li>Procedimiento: Uso exclusivo de ropa interior sin etiquetas ni costuras.</li><li>Función: Evitar sobrecarga somatosensorial.</li><li>Riesgo: Visto como “capricho” o “exigencia innecesaria”.</li></ul></details><details><summary>6 Caso “Guantes de Algodón Nocturnos”</summary><ul><li>Contexto: TEPT -C somatizado.</li><li>Procedimiento: Dormir con guantes suaves para reducir hipersensibilidad táctil.</li><li>Función: Barrera sensorial que permite descanso.</li><li>Riesgo: Considerado “extraño” o “obsesivo”.</li></ul></details><details><summary>7 Caso “Botella Táctil en Reuniones”</summary><ul><li>Contexto: TEPT -C - ansiedad social.</li><li>Procedimiento: Sostener una botella con textura específica durante charlas.</li><li>Función: Focalización sensorial para modular ansiedad.</li><li>Riesgo: Interpretado como “falta de atención” o “jugueteo”.</li></ul></details><details><summary>8 Caso “Reloj Analógico con Segundero”</summary><ul><li>Contexto: TEPT -C temporal (desincronía cronobiológica).</li><li>Procedimiento: Uso exclusivo de reloj analógico con segundero visible.</li><li>Función: Anclaje rítmico para regular percepción del tiempo.</li><li>Riesgo: Visto como “anticuado” o “innecesario”.</li></ul></details><details><summary>9 Caso “Plantillas Sensoriales Personalizadas”</summary><ul><li>Contexto: Disociación leve - autismo.</li><li>Procedimiento: Zapatos con plantillas de textura variable o ligera vibración.</li><li>Función: Grounding somático continuo.</li><li>Riesgo: Ignorado como “detalle menor”.</li></ul></details><details><summary>10 Caso “Ropa Interior Infantil Simbólica”</summary><ul><li>Contexto: TEPT -C por abandono emocional.</li><li>Procedimiento: Uso de ropa interior con diseños “infantiles” (ej.: dibujos) bajo ropa adulta.</li><li>Función: Creación de espacio seguro privado; autocompasión corporal.</li><li>Riesgo: Malinterpretado como fetichismo o inmadurez.</li></ul></details><details><summary>11 Caso “Prenda Interior Húmeda con Agua Tibia”</summary><ul><li>Contexto: TEPT -C somatizado - necesidad de regulación vagal.</li><li>Procedimiento: Llevar una prenda ligeramente humedecida contra la piel.</li><li>Función: Estimulación térmica que calma el sistema nervioso.</li><li>Riesgo: Confundido con negligencia o conducta inapropiada.</li></ul></details><details><summary>12 Caso “Pañal Nocturno Simbólico”</summary><ul><li>Contexto: TEPT -C disociativo por negligencia infantil.</li><li>Procedimiento: Uso de pañal de tela con bombacha de goma solo para dormir.</li><li>Función: Contención táctil, reparación simbólica del cuidado primario.</li><li>Riesgo: Malinterpretado como regresión o inmadurez.</li></ul></details><details><summary>13 Caso “Vendaje Torácico Suave”</summary><ul><li>Contexto: Disociación por trauma relacional.</li><li>Procedimiento: Vendaje elástico no restrictivo alrededor del torso.</li><li>Función: Sensación de “estar contenido/a” para evitar desintegración del yo.</li><li>Riesgo: Asociado erróneamente con autolesión o trastornos alimenticios.</li></ul></details><details><summary>14 Caso “Calcetines en el Templo”</summary><ul><li>Contexto: Autismo - hipersensibilidad táctil.</li><li>Procedimiento: Uso de calcetines en entornos donde se espera ir descalzo.</li><li>Función: Reducir dolor sensorial y mantener conexión corporal.</li><li>Riesgo: Visto como desobediencia a costumbres locales.</li></ul></details><details><summary>15 Caso “Ropa Interior de Otro Género (por Textura)”</summary><ul><li>Contexto: TEPT -C - necesidad sensorial específica.</li><li>Procedimiento: Uso de ropa interior diseñada para otro género por su corte o suavidad.</li><li>Función: Regulación táctil, no identidad de género.</li><li>Riesgo: Confundido con cuestiones de identidad sexual.</li></ul></details><details><summary>16 Caso “Pañal Desechable Bajo Traje Formal”</summary><ul><li>Contexto: TEPT -C disociativo.</li><li>Procedimiento: Uso de pañal desechable como prenda interior en eventos sociales.</li><li>Función: Contención constante para prevenir colapso emocional.</li><li>Riesgo: Juzgado como “engaño” o “falta de madurez”.</li></ul></details><details><summary>17 Caso “Fajador Abdominal Nocturno”</summary><ul><li>Contexto: Trauma abdominal - sensación de vacío emocional.</li><li>Procedimiento: Prenda enrollada suavemente alrededor del abdomen al dormir.</li><li>Función: “Sostén interno” que reduce ansiedad visceral.</li><li>Riesgo: Asociado con control de peso o trastornos corporales.</li></ul></details><details><summary>18 Caso “Ropa Interior de Seda o Neopreno”</summary><ul><li>Contexto: Autismo - búsqueda de respuesta táctil específica.</li><li>Procedimiento: Uso de materiales no convencionales por su efecto regulatorio.</li><li>Función: Activación selectiva de vías sensoriales calmantes.</li><li>Riesgo: Visto como “lujo innecesario” o “excentricidad”.</li></ul></details><details><summary>19 Caso “Bufanda como Segunda Piel”</summary><ul><li>Contexto: TEPT -C relacional.</li><li>Procedimiento: Llevar siempre una bufanda envolviendo cuello y hombros.</li><li>Función: Simboliza protección y límite interpersonal.</li><li>Riesgo: Considerado “exagerado” en climas cálidos.</li></ul></details><details><summary>20 Caso “Anillo Giratorio de Autorregulación”</summary><ul><li>Contexto: TEPT -C - flashbacks.</li><li>Procedimiento: Uso de anillo con parte móvil para frotar en momentos de estrés.</li><li>Función: Interrupción sensorial de activación traumática.</li><li>Riesgo: Visto como “juguete” o “distracción”.</li></ul></details><details><summary>21 Caso “Pijama de Día en Casa”</summary><ul><li>Contexto: TEPT -C - agotamiento regulatorio.</li><li>Procedimiento: Usar ropa de dormir durante el día en entornos seguros.</li><li>Función: Reducir demanda sensorial y preservar energía emocional.</li><li>Riesgo: Juzgado como “falta de esfuerzo” o “depresión”.</li></ul></details><details><summary>22 Caso “Chaleco de Peso Ligero”</summary><ul><li>Contexto: Autismo - ansiedad sensorial.</li><li>Procedimiento: Chaleco con peso distribuido (menos del 10% del peso corporal).</li><li>Función: Estimulación propioceptiva para calma neurológica.</li><li>Riesgo: Malinterpretado como “castigo” o “restricción”.</li></ul></details><details><summary>23 Caso “Gafas con Filtro de Luz Ámbar”</summary><ul><li>Contexto: TEPT -C - sobrecarga visual.</li><li>Procedimiento: Uso de lentes con tinte específico en interiores.</li><li>Función: Reducir estimulación visual que activa alarma.</li><li>Riesgo: Visto como “afectación” o “rareza”.</li></ul></details><details><summary>24 Caso “Brazaletes Táctiles en Muñeca”</summary><ul><li>Contexto: Disociación por trauma interpersonal.</li><li>Procedimiento: Uso de brazaletes con texturas variables.</li><li>Función: Anclaje táctil para mantener presencia corporal.</li><li>Riesgo: Confundido con moda o accesorio estético.</li></ul></details><details><summary>25 Caso “Ropa Interior de la Infancia (Talla Adulta)”</summary><ul><li>Contexto: TEPT -C por ruptura del apego temprano.</li><li>Procedimiento: Reproducir prenda de la infancia en talla adulta.</li><li>Función: Reconexión simbólica con etapa previa al trauma.</li><li>Riesgo: Malinterpretado como fijación patológica.</li></ul></details><details><summary>26 Caso “Múltiples Mantas en Verano”</summary><ul><li>Contexto: TEPT -C nocturno - miedo a la desintegración.</li><li>Procedimiento: Dormir envuelto/a en varias mantas, incluso con calor.</li><li>Función: Recrear “útero seguro” para descanso reparador.</li><li>Riesgo: Visto como “irracional” o “autodestructivo”.</li></ul></details><details><summary>27 Caso “Pulsera con Cuentas para Frotar”</summary><ul><li>Contexto: TEPT-C - hiperalerta.</li><li>Procedimiento: Pulsera con cuentas texturizadas para estimulación repetitiva.</li><li>Función: Autorregulación no verbal en entornos sociales.</li><li>Riesgo: Juzgado como “nerviosismo” o “falta de compostura”.</li></ul></details><details><summary>28 Caso “Camiseta Interior con Bolsillo Simbólico”</summary><ul><li>Contexto: TEPT -C - necesidad de objeto transicional.</li><li>Procedimiento: Camiseta con pequeño bolsillo interno que contiene un objeto seguro.</li><li>Función: Acceso discreto a anclaje emocional.</li><li>Riesgo: Ignorado o malinterpretado como “extraño”.</li></ul></details><details><summary>29 Caso “Zapatos Siempre Atados, Nunca Desabrochados”</summary><ul><li>Contexto: Autismo - necesidad de predictibilidad sensorial.</li><li>Procedimiento: Mantener zapatos siempre bien ajustados, sin holgura.</li><li>Función: Evitar sensación de “desconexión” con el suelo.</li><li>Riesgo: Visto como “rigidez” o “obsesión”.</li></ul></details><details><summary>30 Caso “Ropa Interior con Etiqueta de Afirmación”</summary><ul><li>Contexto: TEPT -C - vergüenza internalizada.</li><li>Procedimiento: Prenda con etiqueta cosida que dice “Seguro/a”, “Digno/a”, etc.</li><li>Función: Refuerzo simbólico de valor personal en momentos de crisis.</li><li>Riesgo: Invisibilizado o considerado “superstición”.</li></ul></details> --- <div class="tip custom-block"><p class="custom-block-title">Bienvenido</p><p>Nota: ClearPath es un recurso educativo independiente, creado por miembros fieles para apoyar a lideres, familias y profesionales. No esta afiliado ni oficialmente respaldado por La Iglesia de Jesucristo de los Santos de los Ultimos Dias.</p><p>Cada camino hacia la claridad comienza con un primer paso. El tuyo comienza aqui.</p></div>',32)])])}const C=e(s,[["render",n]]);export{p as __pageData,C as default};
