import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.B9E6zNJU.js";const q=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"pt/prenc-lista.md","filePath":"pt/prenc-lista.md"}'),l={name:"pt/prenc-lista.md"};function s(u,o,r,m,n,d){return e(),a("div",null,[...o[0]||(o[0]=[t("<details><summary>1 Caso &quot;Roupa Íntima de Compressão Invisível&quot;</summary><ul><li>Contexto: TEPT-C relacional - ansiedade social.</li><li>Procedimento: Camiseta interior de compressão sob roupa formal em sacramentais.</li><li>Função: &quot;Abraço invisível&quot; que reduz hipervigilância.</li><li>Risco: Visto como &quot;excesso&quot; ou &quot;falta de simplicidade&quot;.</li></ul></details><details><summary>2 Caso &quot;Manta Transicional Portátil&quot;</summary><ul><li>Contexto: Autismo - TEPT-C.</li><li>Procedimento: Levar uma manta pequena enrolada na mochila para usar em crises sensoriais.</li><li>Função: Objeto de contenção em ambientes imprevisíveis.</li><li>Risco: Considerado &quot;infantil&quot; ou &quot;inapropriado&quot; em adultos.</li></ul></details><details><summary>3 Caso &quot;Meias Duplas no Verão&quot;</summary><ul><li>Contexto: Dissociação por trauma precoce.</li><li>Procedimento: Usar dois pares de meias grossas mesmo no calor.</li><li>Função: Âncora proprioceptiva para reduzir sensação de &quot;flutuar&quot;.</li><li>Risco: Julgado como &quot;negligência&quot; ou &quot;falta de higiene&quot;.</li></ul></details><details><summary>4 Caso &quot;Prenda de Figura Segura&quot;</summary><ul><li>Contexto: Apego traumático.</li><li>Procedimento: Sempre levar um cachecol ou colete de uma pessoa de confiança.</li><li>Função: Ativação simbólica de redes de apego interno.</li><li>Risco: Mal interpretado como &quot;dependência emocional&quot;.</li></ul></details><details><summary>5 Caso &quot;Roupa Íntima Sem Costuras&quot;</summary><ul><li>Contexto: Autismo - hipersensibilidade sensorial.</li><li>Procedimento: Uso exclusivo de roupa íntima sem etiquetas nem costuras.</li><li>Função: Evitar sobrecarga somatossensorial.</li><li>Risco: Visto como &quot;capricho&quot; ou &quot;exigência desnecessária&quot;.</li></ul></details><details><summary>6 Caso &quot;Luvas de Algodão Noturnas&quot;</summary><ul><li>Contexto: TEPT-C somatizado.</li><li>Procedimento: Dormir com luvas macias para reduzir hipersensibilidade tátil.</li><li>Função: Barreira sensorial que permite descanso.</li><li>Risco: Considerado &quot;estranho&quot; ou &quot;obsessivo&quot;.</li></ul></details><details><summary>7 Caso &quot;Garrafa Tátil em Reuniões&quot;</summary><ul><li>Contexto: TEPT-C - ansiedade social.</li><li>Procedimento: Segurar uma garrafa com textura específica durante palestras.</li><li>Função: Focalização sensorial para modular ansiedade.</li><li>Risco: Interpretado como &quot;falta de atenção&quot; ou &quot;brincadeira&quot;.</li></ul></details><details><summary>8 Caso &quot;Relógio Analógico com Ponteiro de Segundos&quot;</summary><ul><li>Contexto: TEPT-C temporal (dessincronia cronobiológica).</li><li>Procedimento: Uso exclusivo de relógio analógico com ponteiro de segundos visível.</li><li>Função: Âncora rítmica para regular percepção do tempo.</li><li>Risco: Visto como &quot;antiquado&quot; ou &quot;desnecessário&quot;.</li></ul></details><details><summary>9 Caso &quot;Palmilhas Sensoriais Personalizadas&quot;</summary><ul><li>Contexto: Dissociação leve - autismo.</li><li>Procedimento: Sapatos com palmilhas de textura variável ou leve vibração.</li><li>Função: Aterramento somático contínuo.</li><li>Risco: Ignorado como &quot;detalhe menor&quot;.</li></ul></details><details><summary>10 Caso &quot;Roupa Íntima Infantil Simbólica&quot;</summary><ul><li>Contexto: TEPT-C por abandono emocional.</li><li>Procedimento: Uso de roupa íntima com desenhos &quot;infantis&quot; (ex.: desenhos) sob roupa adulta.</li><li>Função: Criação de espaço seguro privado; autocompaixão corporal.</li><li>Risco: Mal interpretado como fetichismo ou imaturidade.</li></ul></details><details><summary>11 Caso &quot;Prenda Interior Ligeiramente Úmida com Água Morna&quot;</summary><ul><li>Contexto: TEPT-C somatizado - necessidade de regulação vagal.</li><li>Procedimento: Levar uma peça ligeiramente umedecida contra a pele.</li><li>Função: Estimulação térmica que acalma o sistema nervoso.</li><li>Risco: Confundido com negligência ou comportamento inapropriado.</li></ul></details><details><summary>12 Caso &quot;Fralda Noturna Simbólica&quot;</summary><ul><li>Contexto: TEPT-C dissociativo por negligência infantil.</li><li>Procedimento: Uso de fralda de pano com calcinha de borracha apenas para dormir.</li><li>Função: Contenção tátil, reparação simbólica do cuidado primário.</li><li>Risco: Mal interpretado como regressão ou imaturidade.</li></ul></details><details><summary>13 Caso &quot;Bandagem Torácica Suave&quot;</summary><ul><li>Contexto: Dissociação por trauma relacional.</li><li>Procedimento: Bandagem elástica não restritiva ao redor do tórax.</li><li>Função: Sensação de &quot;estar contido/a&quot; para evitar desintegração do eu.</li><li>Risco: Associado erroneamente com autolesão ou transtornos alimentares.</li></ul></details><details><summary>14 Caso &quot;Meias no Templo&quot;</summary><ul><li>Contexto: Autismo - hipersensibilidade tátil.</li><li>Procedimento: Uso de meias em ambientes onde se espera ir descalço.</li><li>Função: Reduzir dor sensorial e manter conexão corporal.</li><li>Risco: Visto como desobediência aos costumes locais.</li></ul></details><details><summary>15 Caso &quot;Roupa Íntima de Outro Gênero (por Textura)&quot;</summary><ul><li>Contexto: TEPT-C - necessidade sensorial específica.</li><li>Procedimento: Uso de roupa íntima projetada para outro gênero por seu corte ou maciez.</li><li>Função: Regulação tátil, não identidade de gênero.</li><li>Risco: Confundido com questões de identidade sexual.</li></ul></details><details><summary>16 Caso &quot;Fralda Descartável Sob Terno Formal&quot;</summary><ul><li>Contexto: TEPT-C dissociativo.</li><li>Procedimento: Uso de fralda descartável como roupa íntima em eventos sociais.</li><li>Função: Contenção constante para prevenir colapso emocional.</li><li>Risco: Julgado como &quot;engano&quot; ou &quot;falta de maturidade&quot;.</li></ul></details><details><summary>17 Caso &quot;Cinta Abdominal Noturna&quot;</summary><ul><li>Contexto: Trauma abdominal - sensação de vazio emocional.</li><li>Procedimento: Peça enrolada suavemente ao redor do abdômen ao dormir.</li><li>Função: &quot;Sustentação interna&quot; que reduz ansiedade visceral.</li><li>Risco: Associado com controle de peso ou transtornos corporais.</li></ul></details><details><summary>18 Caso &quot;Roupa Íntima de Seda ou Neoprene&quot;</summary><ul><li>Contexto: Autismo - busca de resposta tátil específica.</li><li>Procedimento: Uso de materiais não convencionais por seu efeito regulatório.</li><li>Função: Ativação seletiva de vias sensoriais calmantes.</li><li>Risco: Visto como &quot;luxo desnecessário&quot; ou &quot;excentricidade&quot;.</li></ul></details><details><summary>19 Caso &quot;Cachecol como Segunda Pele&quot;</summary><ul><li>Contexto: TEPT-C relacional.</li><li>Procedimento: Sempre levar um cachecol envolvendo pescoço e ombros.</li><li>Função: Simboliza proteção e limite interpessoal.</li><li>Risco: Considerado &quot;exagerado&quot; em climas quentes.</li></ul></details><details><summary>20 Caso &quot;Anel Giratório de Autorregulação&quot;</summary><ul><li>Contexto: TEPT-C - flashbacks.</li><li>Procedimento: Uso de anel com parte móvel para esfregar em momentos de estresse.</li><li>Função: Interrupção sensorial de ativação traumática.</li><li>Risco: Visto como &quot;brinquedo&quot; ou &quot;distração&quot;.</li></ul></details><details><summary>21 Caso &quot;Pijama de Dia em Casa&quot;</summary><ul><li>Contexto: TEPT-C - esgotamento regulatório.</li><li>Procedimento: Usar roupa de dormir durante o dia em ambientes seguros.</li><li>Função: Reduzir demanda sensorial e preservar energia emocional.</li><li>Risco: Julgado como &quot;falta de esforço&quot; ou &quot;depressão&quot;.</li></ul></details><details><summary>22 Caso &quot;Colete de Peso Leve&quot;</summary><ul><li>Contexto: Autismo - ansiedade sensorial.</li><li>Procedimento: Colete com peso distribuído (menos de 10% do peso corporal).</li><li>Função: Estimulação proprioceptiva para calma neurológica.</li><li>Risco: Mal interpretado como &quot;castigo&quot; ou &quot;restrição&quot;.</li></ul></details><details><summary>23 Caso &quot;Óculos com Filtro de Luz Âmbar&quot;</summary><ul><li>Contexto: TEPT-C - sobrecarga visual.</li><li>Procedimento: Uso de óculos com tinta específica em ambientes internos.</li><li>Função: Reduzir estimulação visual que ativa alarme.</li><li>Risco: Visto como &quot;afetação&quot; ou &quot;estranheza&quot;.</li></ul></details><details><summary>24 Caso &quot;Pulseiras Táteis no Pulso&quot;</summary><ul><li>Contexto: Dissociação por trauma interpessoal.</li><li>Procedimento: Uso de pulseiras com texturas variáveis.</li><li>Função: Âncora tátil para manter presença corporal.</li><li>Risco: Confundido com moda ou acessório estético.</li></ul></details><details><summary>25 Caso &quot;Roupa Íntima da Infância (Tamanho Adulto)&quot;</summary><ul><li>Contexto: TEPT-C por ruptura do apego precoce.</li><li>Procedimento: Reproduzir peça da infância em tamanho adulto.</li><li>Função: Reconexão simbólica com etapa prévia ao trauma.</li><li>Risco: Mal interpretado como fixação patológica.</li></ul></details><details><summary>26 Caso &quot;Múltiplas Mantas no Verão&quot;</summary><ul><li>Contexto: TEPT-C noturno - medo da desintegração.</li><li>Procedimento: Dormir enrolado/a em várias mantas, mesmo com calor.</li><li>Função: Recriar &quot;útero seguro&quot; para descanso reparador.</li><li>Risco: Visto como &quot;irracional&quot; ou &quot;autodestrutivo&quot;.</li></ul></details><details><summary>27 Caso &quot;Pulseira com Contas para Esfregar&quot;</summary><ul><li>Contexto: TEPT-C - hiperalerta.</li><li>Procedimento: Pulseira com contas texturizadas para estimulação repetitiva.</li><li>Função: Autorregulação não verbal em ambientes sociais.</li><li>Risco: Julgado como &quot;nervosismo&quot; ou &quot;falta de compostura&quot;.</li></ul></details><details><summary>28 Caso &quot;Camiseta Interior com Bolso Simbólico&quot;</summary><ul><li>Contexto: TEPT-C - necessidade de objeto transicional.</li><li>Procedimento: Camiseta com pequeno bolso interno que contém um objeto seguro.</li><li>Função: Acesso discreto a âncora emocional.</li><li>Risco: Ignorado ou mal interpretado como &quot;estranho&quot;.</li></ul></details><details><summary>29 Caso &quot;Sapatos Sempre Amarrados, Nunca Desamarrados&quot;</summary><ul><li>Contexto: Autismo - necessidade de previsibilidade sensorial.</li><li>Procedimento: Manter sapatos sempre bem ajustados, sem folga.</li><li>Função: Evitar sensação de &quot;desconexão&quot; com o chão.</li><li>Risco: Visto como &quot;rigidez&quot; ou &quot;obsessão&quot;.</li></ul></details><details><summary>30 Caso &quot;Roupa Íntima com Etiqueta de Afirmação&quot;</summary><ul><li>Contexto: TEPT-C - vergonha internalizada.</li><li>Procedimento: Peça com etiqueta costurada que diz &quot;Seguro/a&quot;, &quot;Digno/a&quot;, etc.</li><li>Função: Reforço simbólico de valor pessoal em momentos de crise.</li><li>Risco: Invisibilizado ou considerado &quot;superstição&quot;.</li></ul></details>",30)])])}const p=i(l,[["render",s]]);export{q as __pageData,p as default};
